def round_matrix_values_in_directory(directory_path, decimals):
    """
    Reads all CSV files in the given directory, rounds each numeric value to a specified number of decimal points,
    and saves the modified matrices back to the same files.

    Parameters:
    directory_path (str): Path to the directory containing the CSV files.
    decimals (int): Number of decimal points to round the values to.
    """
    # Iterate over all files in the directory
    for filename in os.listdir(directory_path):
        if filename.endswith(".csv"):
            file_path = os.path.join(directory_path, filename)
            
            # Read the CSV file into a DataFrame
            df = pd.read_csv(file_path)

            # Round each numeric value to the specified number of decimal points
            df = df.applymap(lambda x: round(x, decimals) if isinstance(x, (int, float)) else x)

            # Save the updated DataFrame to the same CSV file
            df.to_csv(file_path, index=False)

            print(f"Rounded values in {filename} and saved the updated file.")

def integrate_and_process(quantification_folder, decimals=3, remove=True):
    # Validate the number of decimals
    if decimals < 1 or decimals > 7:
        raise ValueError("Decimals should be between 1 and 7.")

    # Round values in all CSV files in the quantification folder
    round_matrix_values_in_directory(quantification_folder, decimals)

    # Load the SMN3 data
    smn3_df = get_smn3_data()

    # Initialize dictionaries to hold dataframes categorized by localization
    localization_files = {
        "Nuclear": None,
        "Cytoplasmic": None,
        "Cell": None
    }

    # Variable to hold the eddy object dataframe
    eddy_file = None

    # Assign files to localizations based on the second part of their name
    file_prefix = None
    for file in os.listdir(quantification_folder):
        if file.endswith(".csv"):
            parts = file.split("_")
            if len(parts) > 1:
                category = parts[1].split(".")[0]  # Ensure we get rid of .csv extension for comparison
                file_path = os.path.join(quantification_folder, file)
                if category == "nucleiRing":
                    localization_files["Nuclear"] = pd.read_csv(file_path)
                elif category == "cytoRing":
                    localization_files["Cytoplasmic"] = pd.read_csv(file_path)
                elif category == "cellRing":
                    localization_files["Cell"] = pd.read_csv(file_path)
                    eddy_file = localization_files["Cell"]
            if file_prefix is None:
                file_prefix = parts[0]

    # Check if eddy file is loaded
    if eddy_file is None:
        raise ValueError("Eddy file with 'cellRing' extension not found in the 'quantification' folder.")

    # Iterate over each column in eddy file
    for column in eddy_file.columns:
        if column in smn3_df["Standardized Marker Name"].values:
            localization = smn3_df[smn3_df["Standardized Marker Name"] == column]["Categorized Localization"].values[0]
            
            if localization == "Nuclear":
                if column in localization_files["Nuclear"].columns:
                    eddy_file[column] = localization_files["Nuclear"][column]
            elif localization == "Cytoplasmic":
                if column in localization_files["Cytoplasmic"].columns:
                    eddy_file[column] = localization_files["Cytoplasmic"][column]
            elif localization == "Cell":
                if column in localization_files["Cell"].columns:
                    eddy_file[column] = localization_files["Cell"][column]

    # Save the modified eddy object
    output_file = os.path.join(quantification_folder, f"{file_prefix}_eddy.csv")
    eddy_file.to_csv(output_file, index=False)

    print(f"Eddy object saved to {output_file}")

    # Remove all files in the quantification folder except the eddy file if remove is True
    if remove:
        for file in os.listdir(quantification_folder):
            file_path = os.path.join(quantification_folder, file)
            if file_path != output_file:
                os.remove(file_path)
                print(f"Removed {file}")

